package forscene.core.entities.toTest.sprites;

// TODO: Auto-generated Javadoc
/**
 * The Class AbstractSprite.
 *
 * @author blackdevil
 */
public class AbstractSprite {
  
  /** The ID. */
  private String ID   = "";
  
  /** The name. */
  private String name = "";

  /**
   * Gets the iD.
   *
   * @return the iD
   */
  public String getID() {
    return ID;
  }

  /**
   * Sets the iD.
   *
   * @param iD the iD to set
   */
  public void setID(String iD) {
    ID = iD;
  }

  /**
   * Gets the name.
   *
   * @return the name
   */
  public String getName() {
    return name;
  }

  /**
   * Sets the name.
   *
   * @param name the name to set
   */
  public void setName(String name) {
    this.name = name;
  }
}
